﻿using System.Collections.Generic;
using System.Xml;

class ShoutyReportProcessor
{
    IList<MileageClaim> mileageClaims;
    IStatsService statsService;
    IDictionary<string, IList<MileageClaim>> totalMileage;

    public ShoutyReportProcessor(IList<MileageClaim> mileageClaims, IStatsService statsService)
    {
        this.mileageClaims = mileageClaims;
        this.statsService = statsService;
        this.totalMileage = new Dictionary<string, IList<MileageClaim>>();
    }

    public IList<EcoStat> Process()
    {
        foreach(var claim in mileageClaims)
        {
            if (!totalMileage.ContainsKey(claim.Name))
            {
                var mileage = new List<MileageClaim>();
                mileage.Add(claim);
                totalMileage.Add(claim.Name, mileage); 
            }
            else
            {
                var mileage = totalMileage[claim.Name];
                mileage.Add(claim);
                totalMileage[claim.Name] = mileage;                
            }
        }

        var ecoStats = new List<EcoStat>();

        foreach(KeyValuePair<string, IList<MileageClaim>> entry in totalMileage)
        {
            var name = entry.Key;
            var totalMileage = entry.Value;
            var sumMiles = 0;
            foreach(var mileage in totalMileage)
            {
                sumMiles += mileage.Miles;
            }

            var sumRevenue = (decimal) 0;
            foreach(var claim in totalMileage)
            {
                var revenue = this.statsService.GetRevenue(claim.CustomerID);
                sumRevenue += revenue;
            }

            var revenuePerMile = 0.0f;
            if ((float)sumMiles == 0) 
            {
                revenuePerMile = 0;
            }
            else
            {
                revenuePerMile = (float)sumRevenue / (float)sumMiles;
            }

            var item = new EcoStat(name, revenuePerMile);
            ecoStats.Add(item);            
        } 
        return ecoStats;
    }

    public void WriteEcoStatsReport(IList<EcoStat> results)
    {
        XmlDocument reportXml = new XmlDocument();
        reportXml.LoadXml("<?xml version=\"1.0\" encoding=\"UTF-8\"?><ecoReport/>");

        foreach(var ecoStat  in results)
        {
            var salesPersonName = ecoStat.SalesPersonName;
            var revenuePerMile = ecoStat.RevenuePerMile;
            XmlElement node = reportXml.CreateElement(string.Empty, "ecoStat", string.Empty);
            node.SetAttribute("SalespersonName", salesPersonName);
            node.SetAttribute("RevenuePerMile", System.Convert.ToString(revenuePerMile));
            reportXml.DocumentElement.AppendChild(node);
        }
        reportXml.Save("report.xml");
    }

    public void SetEcoStats(IList<EcoStat> results, int month, int year)
    {
        string requestXml = "<EcoStats month=\"" + month + "\" year=\"" + year + "\" >";
        foreach(var ecoStat in results)
        {
            requestXml += "<EcoStat salesPersonName=\"" + ecoStat.SalesPersonName + "\" revenuePerMile=\"" + ecoStat.RevenuePerMile + "\" >";        	            
        }
        requestXml += "</EcoStats>";
        this.statsService.SetEcoStats(requestXml);        
    }
}

