﻿using System.Collections.Generic;
using System.Xml;
using System;
using System.Linq;

class ShoutyReportProcessor
{
    IList<MileageClaim> mileageClaims;
    IStatsService statsService;
    IEcoReporter ecoReporter;

    public ShoutyReportProcessor(IList<MileageClaim> mileageClaims) : this(mileageClaims, new ProductionStatsService(), new XmlEcoReporter()) { }

    public ShoutyReportProcessor(IList<MileageClaim> mileageClaims, IStatsService statsService, IEcoReporter ecoReporter)
    {
        this.mileageClaims = mileageClaims;
        this.statsService = statsService;
        this.ecoReporter = ecoReporter;
    }

    public void Process()
    {
        var groupedMileageClaims = new Dictionary<string, List<MileageClaim>>();

        groupedMileageClaims = mileageClaims
            .GroupBy(c => c.Name)
            .ToDictionary(g => g.Key, g => g.ToList());

        var ecoReportData = new Dictionary<string, float>();

        foreach (var groupedClaim in groupedMileageClaims)
        {
            var revenue = 0m;
            var miles = 0m;

            foreach (var claim in groupedClaim.Value) {
                var currentRevenue = GetRevenue(claim);
                revenue += currentRevenue;
                miles += claim.Miles;
            }

            var revenuePerMile = (float)revenue / (float)miles;
            ecoReportData.Add(groupedClaim.Key, revenuePerMile);
        }

        ecoReporter.Save(ecoReportData);
    }

    private decimal GetRevenue(MileageClaim claim){
        string requestXml = "<Customer id=\"" + claim.CustomerID + "\"/>";
        string responseXml = statsService.GetRevenueForCustomer(requestXml);

        var responseDocument = new XmlDocument();
        responseDocument.LoadXml(responseXml);
        var currentRevenue = decimal.Parse(responseDocument.DocumentElement.Attributes["revenue"].Value);
		return currentRevenue;
    }
}

