using System.Collections.Generic;
using System.Xml;

public class XmlEcoReporter : IEcoReporter {
    public void Save(IDictionary<string, float> ecoReportData) {
        XmlDocument reportXml = new XmlDocument();
        reportXml.LoadXml("<?xml version=\"1.0\" encoding=\"UTF-8\"?><ecoReport/>");

        foreach (var item in ecoReportData) {
            XmlElement node = reportXml.CreateElement(string.Empty, "ecoStat", string.Empty);
            node.SetAttribute("SalespersonName", item.Key);
            node.SetAttribute("RevenuePerMile", System.Convert.ToString(item.Value));
            reportXml.DocumentElement.AppendChild(node);
        }

        reportXml.Save("report.xml");
    }
}

